    def get_licensee_details(self, url):
        # Fetch and parse detailed licensee information from the given URL.
        try:
            payload = {}
            response = requests.request("GET", url, headers=self.headers, data=payload)
            soup = BeautifulSoup(response.text, 'html.parser')
            # Extract licensee details from specific HTML elements
            name = soup.find('div', class_='panel-heading').text.strip().split(' - ')[0]
            license_type = soup.find('div', class_='panel-heading').text.strip().split(' - ')[1]
            license_number = soup.find('div', string='License #:').find_next('div').text.strip()
            status = soup.find('span', class_='label').text.strip()
            issue_date = soup.find('div', string='Issue Date:').find_next('div').text.strip()
            # Handle multiple possible date labels (Renewal Date or Expire Date)
            date_label = soup.find('div', string=['Renewal Date:', 'Expire Date:'])
            expire_date = date_label.find_next('div').text.strip() if date_label else "Not found"
            # Return the extracted details as a dictionary.
            return {
                "Full_Name": name,
                "License_Type": license_type,
                "License_Number": license_number,
                "Status": status,
                "Professional": license_type,
                "Issued": issue_date,
                "Expired": expire_date
            }
        except Exception as e:
            # Log errors encountered while fetching licensee details.
            logging.error(f"Error fetching details for {url}: {e}")
            return None

    def save_to_csv(self, data):
        # Save licensee data to the CSV file.
        file_exists = os.path.isfile(self.output_file)
        try:
            with open(self.output_file, 'a', newline='', encoding='utf-8') as csvfile:
                writer = csv.DictWriter(csvfile, fieldnames=self.csv_headers)
                # Write headers only if the file doesn't already exist
                if not file_exists:
                    writer.writeheader()
                writer.writerow(data)
        except PermissionError:
            # Log error if the CSV file is open or inaccessible.
            logging.error(f"File {self.output_file} is open. Could not write data.")

    def run(self):
        # Main crawling function to iterate through name and license type combinations.
        
        combinations = [''.join(pair) for pair in itertools.product("ABCDEFGHIJKLMNOPQRSTUVWXYZ", repeat=2)]
        license_types = ["DOP", "MDP"]
        processed_links = set()

        for name in combinations:
            for l_type in license_types:
                logging.info(f"Searching for {name} ({l_type})")
                try:
                    # Fetch licensee links for the given combination.
                    links = self.get_licensee_links(name, l_type)
                    logging.info(f"Records found for {name} ({l_type}): {len(links)}")

                    for link in links:
                         # Skip already processed links to avoid duplication.
                        if link in processed_links:
                            logging.info(f"Duplicate link skipped: {link}")
                            continue

                        # Fetch licensee details and save to CSV.
                        details = self.get_licensee_details(link)
                        if details:
                            self.save_to_csv(details)
                            processed_links.add(link)
                        time.sleep(1)  # Pause to avoid overwhelming the server.
                except Exception as e:
                    # Log errors for the current combination.
                    logging.error(f"Error processing {name} ({l_type}): {e}")
                    continue
        # Log completion of crawling.
        logging.info("Crawling completed.")

